import React from "react";
import { Component } from "react";


class Form extends Component {
    state = {
      name: 'Taylor',
    };
  
    handleNameChange = (e) => {
      const newName = e.target.value;
      this.setState({
        name: newName
      });
    }
  
    render() {
      return (
        <>
          <input value={this.state.name} onChange={this.handleNameChange} />
          <p>Hello, {this.state.name}.</p>
        </>
      );
    }
  }

  export default Form;
