import { Component } from "react";

class Form extends Component{
    state={
        name:'veera'
    };
    handleNameChange=()=>{
        let newName=e.target.value;
        this.setState({
            name:newName
        })
    }
    render(){
        return(
            <div>
                <input value={this.state.name} onChange={this.handleNameChange}/>
                <p>Hello,{this.state.name} </p>
            </div>
        )
    }
}
export default Form;
