import React, { Component } from "react";

class Form extends Component {
  state = {                               //to fix the initial value
    name: "veera",
    age: 28,
  };
  handleNameChange = (e) => {            //arrow function
    let newName = e.target.value;        //create variable to target the input value
    this.setState({                      //to update the value 
      name: newName,
    });
  };
  handleAgeChange = () => {
    // let newAge = e.target.value;
   this.setState((prevState)=>({
    age:prevState.age+1
   }))
  };

  render() {
    return (
      <div>
        <input value={this.state.name} onChange={this.handleNameChange} />     //input tag gives a box, assign two props -value, onChange
        <button onClick={this.handleAgeChange}>Increment Age</button>          //button tag - assign onclick attribute
        <p>
          Hello,{this.state.name} you are {this.state.age}                     //paragraph tag -assign name and age property
        </p>
      </div>
    );
  }
}
export default Form;

